        function command_handler($name, $msg, $origin)
        {
			if (preg_match("/^recipe ([0-9]+)$/i", $msg, $info)) {
				return $this -> show_recipe($info[1]);
			} elseif (preg_match("/^recipe (.+)$/i", $msg)) {
				return $this -> search_recipe($msg);
			} else {
				$this -> bot -> send_help($name);
			}
			return false;
        }

	function show_recipe($number)
	{	
		$bot = $this->bot->botname;
		$results = 0; $msg = "<b>Your recipe details:</b>\n\n";	
		if ($number > 0 && $number < 800) {
		foreach ($this->reciplist as $recipe) {
			if ($number == $recipe[0]) { $final = $recipe[2];
				if (preg_match_all('|/tell {myname} recipe ([0-9]+)|', $recipe[2], $matches)) { 
					foreach ($matches[1] as $match) {
$final = preg_replace("|/tell {myname} recipe ".$match."|","<a href='chatcmd:///tell ".$bot." !recipe ".$match."'>".$match."</a>",$final);
				}	}
			$msg .= $final; }
		}} else { $msg .= "Sorry, no such recipe in database ..."; }
		$blob = $this -> bot -> core("tools") -> make_blob("Your recipe details",$msg);		
		return($blob);		
	}
	
	function search_recipe($kword)
	{	
		$bot = $this->bot->botname;
			if (preg_match('|>([^<]+)</a>|', $kword, $extract))
			{
				$search = $extract[1];
			}
			else 
			{ 
				$extract = explode("recipe ",$kword); $search = $extract[1];
			}
		$results = 0; $msg = "<b>Your search result(s):</b>\n\n";	
		foreach ($this->reciplist as $recipe) {
			if (preg_match("|".$search."|i",$recipe[2])) {
				$results++;
				$msg .= "<a href='chatcmd:///tell ".$bot." !recipe ".$recipe[0]."'>".$recipe[1]."</a>\n";	
			}
		}
		$blob = $this -> bot -> core("tools") -> make_blob($results." corresponding to your search", $msg);		
		return($blob);
	}	
}
